const TT_MARKET_ADDRESS = '0xeF5A0CE54a519B1Db3F350EB902C4cFbf7671D88';

async function main() {
    const App = await init_ethers();

    _print(`Initialized ${App.YOUR_ADDRESS}\n`);

    _print("Reading smart contracts...\n");

    const RAM_PROTOCOL_ADDR = "0x0d4fe8832857Bb557d8CFCf3737cbFc8aE784106";
    const RamProtocol = new ethers.Contract(RAM_PROTOCOL_ADDR, RAM_PROTOCOL_ABI, App.provider);

    const Markets = await RamProtocol.getAllMarkets();

    _print(`Found ${Markets.length} pools.\n`)

    const loadRamMarketData = await loadRamProtocolData(RamProtocol, App);

    for (const market of await Promise.all(Markets.map(loadRamMarketData))) {
        printRamMarketData(market);
    }

    hideLoading();
}

async function getTokenDecimals(market, provider) {
    const rTokenName = await market.name()
    if (rTokenName === 'TT interest bearing token') {
        return 18
    }
    const underlyingAddress = await market.underlying()
    const tt20 = new ethers.Contract(underlyingAddress, MARKET_ABI, provider);
    return tt20.decimals()
}

async function getUnderlyingPrice(market, prices) {
  const rTokenName = await market.name()
  if (rTokenName === 'TT interest bearing token') {
    return prices['0x0000000000000000000000000000000000000000'].usd
  }
  const underlyingAddress = await market.underlying()
  return prices[underlyingAddress].usd
}

async function loadRamProtocolData(protocol, App) {
    const prices = await getTTPrices()
    const blockPerYear = 365 * 24 * 60 * 60;

    return async function loadRamMarketData(address) {
        const market = new ethers.Contract(address, MARKET_ABI, App.provider);

        const [
            decimals,
            underlyingPrice,
            name,
            totalBorrow,
            totalSupply,
            borrowRatePerBlock,
            supplyRatePerBlock,
            exchangeRate,
        ] = await Promise.all([
            getTokenDecimals(market,App.provider),
            getUnderlyingPrice(market, prices),
            market.name(),
            market.totalBorrows(),
            market.totalSupply(),
            market.borrowRatePerBlock(),
            market.supplyRatePerBlock(),
            market.callStatic.exchangeRateCurrent(),
        ])

        const newmarket =  {
            borrowRate: borrowRatePerBlock * blockPerYear * 1e-18 * 100,
            supplyRate: supplyRatePerBlock * blockPerYear * 1e-18 * 100,
            totalBorrow: totalBorrow.div(ethers.BigNumber.from("10").pow(decimals)),
            totalSupply: totalSupply.mul(exchangeRate).div(ethers.BigNumber.from("10").pow(18 + decimals)),
            underlyingPrice,
            uSymbol: name.split(' ')[0],
        }
        return newmarket
    }
}

async function printRamMarketData({
    borrowRate,
    supplyRate,
    totalBorrow,
    totalSupply,
    underlyingPrice,
    uSymbol,
}) {
    _print_bold(`${uSymbol} ($${formatMoney(underlyingPrice)})`);
    _print(`Supplied : ${formatMoney(totalSupply)} ($${formatMoney(totalSupply * underlyingPrice)}) at ${supplyRate.toFixed(2)}% APR`)
    _print(`Borrowed : ${formatMoney(totalBorrow)} ($${formatMoney(totalBorrow * underlyingPrice)}) at ${borrowRate.toFixed(2)}% APR`)

    _print("");
}
